import React from 'react'

const Form = ({inputText, setInputText, todos, setTodos, setStatues, status}) => {

    const inputTextHandler = (e) => {
        setInputText(e.target.value)

    }

 const submetTodoHandler = (e) => {
     e.preventDefault()
     setTodos([
         ...todos, {
             text: inputText,
             completed: false,
             id: Math.random() *1000
         }
     ])
         setInputText("")
 }
    console.log(todos)

    const statuesHandler = (e) => {
        setStatues(e.target.value)
    }






    return (

            <form className="listForm" action="">
                <input value={inputText} onChange={inputTextHandler} className="todo-input" type="text"/>
            <button onClick={submetTodoHandler} type="submit" className="todo-button">
                <i className="fas fa-plus-square"></i>
            </button>
                    <div className="select">
                        <select className="filter-todo" name="todo" id="">
                            <option value="all">all</option>
                            <option value="comleted">completed</option>
                            <option value="uncompleted">uncompleted</option>
                        </select>
                    </div>
            </form>

    );
}

export default Form
