import React,{useState, useEffect} from 'react';
import "./App.css";
import Form from "./Form"
import TodoList from "./TodoList"


const App = () => {
    const [inputText, setInputText] = useState("");
    const [todos, setTodos] = useState([]);
    const [status, setStatues] = useState('all');
    const [filterdTodo, setFilteredTodo] = useState([]);

useEffect(() => {
    filterHandler()
}, [todos, status])    


const filterHandler = () => {
    switch (status) {
        case 'comleted':
            setFilteredTodo(todos.filter(event => event.completed === true))
            break;
        case 'uncompleted':
            setFilteredTodo(todos.filter(event => event.completed === false))
        default:
            setFilteredTodo(todos)
            break;  
    }
}
console.log(filterdTodo)


    return (
        <div className="App">
            <div><h1>Todo App</h1></div>
            <TodoList
                todos={todos}
                setTodos={setTodos}
                filterdTodo={filterdTodo}
            />
            <Form
                setStatues={setStatues}
                status={status}
                todos={todos}
                setTodos={setTodos}
                inputText={inputText}
                setInputText={setInputText}
            />

        </div>
    )
}

export default App;
